mapper.update:
UPDATE user
SET status=1
WHERE code=#{code}

mapper.select:
SELECT *
FROM user
WHERE email=#{email} and password=#{password} and status = 1;

SELECT count(*)
FROM user
WHERE nickname=#{nickname} or email=#{email}

mapper.update:
UPDATE user
SET code=round(rand()*1000000)
WHERE email=#{email}

mapper.select:
SELECT code
FROM user
WHERE email=#{email}

mapper.update:
UPDATE user
SET password=#{password}
WHERE code=#{code}

mapper.select:
SELECT count(*)
FROM user
WHERE email=#{email}

mapper.update:
UPDATE user SET
nickname=#{nickname}, password=#{password}, 
sex=#{sex}, birthday=#{birthday}, location=#{location}, bio=#{bio}
WHERE id=#{id}

UPDATE follow
SET status= true
WHERE follower=#{follower} AND following=#{following}

mapper.select:
SELECT count(*)
FROM follow 
WHERE follower=#{follower} and following=#{following}

mapper.delete:
DELETE
FROM follow
WHERE follower=#{follower} and following=#{following}

mapper.select:
SELECT x
FROM location
WHERE name=#{name}

SELECT y
FROM location
WHERE name=#{name}
