mapper.update:
UPDATE article
SET writer=#{writer}, contents=#{contents}
WHERE id=#{id}

UPDATE article
SET shares=shares+1
WHERE id=#{id}

mapper.select:
SELECT count(*)
FROM article_like
WHERE article_id=#{article_id} AND user_id=#{user_id}

SELECT count(*)
FROM article_like
WHERE article_id=#{article_id}

mapper.delete:
DELETE
FROM article_like
WHERE article_id=#{article_id} AND user_id=#{user_id}

mapper.select:
SELECT user_id
FROM article_like
WHERE article_id=#{article_id}
