mapper.update:
UPDATE tip
SET title=#{title}, contents=#{contents}
WHERE id=#{id}

UPDATE tip
SET hits=hits+1
WHERE id=#{id}

mapper.select:
SELECT user_id
FROM tip_like
WHERE tip_id=#{tip_id}

SELECT count(*)
FROM tip_like
WHERE tip_id=#{tip_id} AND user_id=#{user_id}

mapper.delete:
DELETE 
FROM tip_like
WHERE tip_id=#{tip_id} AND user_id=#{user_id}

mapper.update:
UPDATE tip
SET img_src=#{img_src}
WHERE id=#{id}
